// import HelloWorld from './components/HelloWorld.vue'
import TodoHeader from './components/common/TodoHeader.vue';
import TodoFooter from './components/common/TodoFooter.vue';
import TodoInput from './components/todo-core/TodoInput.vue';
import TodoList from './components/todo-core/TodoList.vue';
import Axios from 'axios';
export default {
  name: 'App',
  components: {
    'TodoHeader': TodoHeader,
    'TodoFooter': TodoFooter,
    'TodoInput': TodoInput,
    'TodoList': TodoList
  },

  data() {
    return {
      todoItems: []
    };
  },

  methods: {
    addTodo(todoItem) {
      // 로컬 스토리지에 데이터를 추가하는 로직
      localStorage.setItem(todoItem, todoItem);
      this.todoItems.push(todoItem);
      let obj = {};
      obj.todoItem = todoItem;
      Axios.post('http://localhost:8080/todo/add', JSON.stringify(obj)).then(function (response) {
        console.log('res', response);
      }).catch(function (error) {
        console.log('res', error);
      });
    },

    clearAll() {
      localStorage.clear();
      this.todoItems = [];
    },

    removeTodo(todoItem, index) {
      localStorage.removeItem(todoItem);
      this.todoItems.splice(index, 1);
    }

  },

  created() {
    if (localStorage.length > 0) {
      for (let i = 0; i < localStorage.length; ++i) {
        this.todoItems.push(localStorage.key(i));
      }
    }
  }

};
