export const loginStateInfo = {

  state : () => ({
    // Vue3GoogleOauthIsAuthorized : false
    userId : '',
    userName : '',
    loginToken : '', // 추후 토큰값을 받아온다.
    isSignIn : false
  }),
  mutations : {
    SET_USERINFO( state, obj ) {
      state.userId   = obj.userId;
      state.userName = obj.userName;
      state.isSignIn = true;      
    }
  },
  actions : {},
  getters : {}

}
