import Axios from 'axios';
import VueCookies from 'vue-cookies';
// import { store } from '../store/index';

/**
 * 토큰 재발급 후 axios 헤더 값 수정
*/
//request 설정
Axios.interceptors.request.use(async function (config) {

  let sendUrl = config.url.split('/');
  let keyStr  = sendUrl[sendUrl.length - 2];

  // 로그인 회원 가입시에는 토큰 인증 절차를 타지 않는다.
  if ( keyStr === 'auth' ) {
    return config;
  }

  //헤더 셋팅
  config.timeout = 10000;
  config.headers['Authorization'] = VueCookies.get('accessToken');
  config.headers['Refresh'] = VueCookies.get('refreshToken');
  config.headers['Content-Type'] = 'application/json';
  console.log('axios interceptors access token ', VueCookies.get('accessToken'));

  return config;
}, function (error) {
  console.log('axios request error : ', error);
  return Promise.reject(error);
});
